import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import Login from './components/Login';
import EmployeeForm from './components/EmployeeForm';
import Dashboard from './components/Dashboard';
import EmployeeList from './components/EmployeeList';
import authService from './services/authService';

const App = () => {
  const [user, setUser] = useState(null);
  const [employees, setEmployees] = useState([]); // List of employees
  const [editingEmployee, setEditingEmployee] = useState(null); // Track which employee is being edited

  const navigate = useNavigate();

  // Handle login
  const handleLogin = async (credentials) => {
    try {
      const data = await authService.login(credentials);
      setUser(data);
      localStorage.setItem('token', data.token);
    } catch (error) {
      console.error('Login failed:', error);
    }
  };

  // Add or update an employee
  const handleEmployeeSubmit = (formData) => {
    if (editingEmployee) {
      // Update existing employee
      const updatedEmployees = employees.map((emp) =>
        emp.email === editingEmployee.email ? formData : emp
      );
      setEmployees(updatedEmployees);
      setEditingEmployee(null); // Clear editing state
    } else {
      // Add new employee
      setEmployees([...employees, formData]);
    }
    navigate('/employee-list'); // Redirect to employee list
  };

  // Select an employee to edit
  const handleEditEmployee = (employee) => {
    setEditingEmployee(employee);
    navigate('/add-employee'); // Navigate to the form for editing
  };

  return (
    <Router>
      <div>
        <h1>Employee Management System</h1>
        <Routes>
          <Route
            path="/login"
            element={<Login onLogin={handleLogin} />}
          />
          <Route
            path="/dashboard"
            element={
              user ? <Dashboard user={user} /> : <div>Please login to access the dashboard.</div>
            }
          />
          <Route
            path="/add-employee"
            element={
              user ? (
                <EmployeeForm
                  onSubmit={handleEmployeeSubmit}
                  existingEmployee={editingEmployee}
                />
              ) : (
                <div>Please login to add or edit an employee.</div>
              )
            }
          />
          <Route
            path="/employee-list"
            element={
              user ? (
                <EmployeeList employees={employees} onEdit={handleEditEmployee} />
              ) : (
                <div>Please login to view the employee list.</div>
              )
            }
          />
          <Route
            path="/"
            element={<div>Welcome to the Employee Management System</div>}
          />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
