import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [employees, setEmployees] = useState([]); // State to store employee data
  const [employee, setEmployee] = useState({
    name: "",
    position: "",
    department: "",
  });

  const [errors, setErrors] = useState({}); // Validation error state

  // Handle change in input fields
  const handleChange = (e) => {
    setEmployee({
      ...employee,
      [e.target.id]: e.target.value,
    });
  };

  // Validation logic
  const validateForm = () => {
    const newErrors = {};
    if (!employee.name) newErrors.name = "Name is required";
    if (!employee.position) newErrors.position = "Position is required";
    if (!employee.department) newErrors.department = "Department is required";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0; // Return true if no errors
  };

  // Handle form submission (add new employee)
  const handleSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      setEmployees([...employees, employee]); // Add new employee to list
      setEmployee({ name: "", position: "", department: "" }); // Reset form fields
      setErrors({}); // Clear errors
    }
  };

  // Handle employee deletion
  const handleDelete = (name) => {
    setEmployees(employees.filter((emp) => emp.name !== name));
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4 text-center">Employee Management System</h1>

      {/* Employee Form */}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">Name</label>
          <input
            type="text"
            id="name"
            className="form-control"
            value={employee.name}
            onChange={handleChange}
          />
          {errors.name && <small className="text-danger">{errors.name}</small>}
        </div>

        <div className="mb-3">
          <label htmlFor="position" className="form-label">Position</label>
          <input
            type="text"
            id="position"
            className="form-control"
            value={employee.position}
            onChange={handleChange}
          />
          {errors.position && <small className="text-danger">{errors.position}</small>}
        </div>

        <div className="mb-3">
          <label htmlFor="department" className="form-label">Department</label>
          <input
            type="text"
            id="department"
            className="form-control"
            value={employee.department}
            onChange={handleChange}
          />
          {errors.department && <small className="text-danger">{errors.department}</small>}
        </div>

        <button type="submit" className="btn btn-primary w-100">Add Employee</button>
      </form>

      {/* Employee List */}
      <div className="mt-4">
        <h3>Employee List</h3>
        <ul className="list-group">
          {employees.map((emp, index) => (
            <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
              {emp.name} - {emp.position} ({emp.department})
              <button className="btn btn-danger btn-sm" onClick={() => handleDelete(emp.name)}>
                Delete
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
